Once you have the Device trait implemented, an Interface can be created: #extern crate smoltcp;
#use smoltcp::wire::Ieee802154Address;
#fn main() {
let ll_addr = Ieee802154Address::Extended([0x1, 0x2, 0x3, 0x4, 0x5, 0x6 0x7, 0x8]); // Create the device:
let mut device = MyDevice::new(ll_addr); // Create the config for RPL:
let mut rpl_config = smoltcp::iface::RplConfig::new(); // Create the configuration for the stack:
let mut config = smoltcp::iface::Config::new();
config.hardware_addr =	Some(ll_addr.into());
config.pan_id = Some(Ieee802154Pan(0xbeef)); // Add the RPL config:
config.rpl = rpl_config; // Create the sockets:
let mut sockets_buffer = [SocketStorage::EMPTY; 1];
let mut sockets = SocketSet::new(&mut sockets_buffer[..]); // Create the interface:
let mut iface = smoltcp::iface::Interface::new(config, device)?; #}
